    <label for="example-text-input" class="col-2 col-form-label">Name of book:</label>
    <div class="col-10">
      <input class="form-control" type="text" value="Artisanal kale" id="example-text-input">
    </div>
  </div>
  <div class="form-group row">
    <label for="example-search-input" class="col-2 col-form-label"></label>
    <div class="col-10">
      <input class="form-control" type="search" value="How do I shoot web" id="example-search-input">
    </div>
  </div>
  <div class="form-group row">
    <label for="example-email-input" class="col-2 col-form-label">Email</label>
    <div class="col-10">
      <input class="form-control" type="email" value="bootstrap@example.com" id="example-email-input">
    </div>
  </div>
  <div class="form-group row">
    <label for="example-url-input" class="col-2 col-form-label">URL</label>
    <div class="col-10">
      <input class="form-control" type="url" value="https://getbootstrap.com" id="example-url-input">
    </div>
  </div>

<div>
    <label for="description">Description:</label><br>
    <textarea id="description" name="description" [(ngModel)]="book.description" rows="4" cols="50"></textarea>
  </div>



<div class="form-group">
    <label for="bookType">Select book type!</label>
    <select class="form-control" id="bookType" [(ngModel)]="book.bookType">
      <option *ngFor="let type of bookTypeOptions" [ngValue]="type">{{ type }}</option>
    </select>
  </div>
