import { Component } from '@angular/core';
import { Product } from 'src/app/interfaces/product';
import { ProductService } from 'src/app/services/product/product.service';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.scss']
})
export class ProductComponent {
  product : Product = {
    name: '',
    price: 0,
    description: '',
    quantity: 0
  };

 constructor(private productService: ProductService) { }

  createProduct(): void {
    this.productService.createProduct(this.product).subscribe(
      response => {
        console.log('Product created successfully:', response);
        // Aici puteți face orice acțiune doriți după ce a fost creat produsul
      },
      error => {
        console.error('Error creating product:', error);
      }
    );
  }
}
<p>product works!</p>



<div class="form-group row">
    <label for="example-text-input" class="col-2 col-form-label">Name of Product:</label>
    <div class="col-10">
      <input class="form-control" type="text"[(ngModel)]="product.name" id="name" >
    </div>
  </div>
  <div class="form-group row">
    <label for="example-search-input" class="col-2 col-form-label">Price:</label>
    <div class="col-10">
      <input class="form-control" type="number" [(ngModel)]="product.price" id="price">
    </div>
  </div>
  <div class="form-group row">
    <label for="example-email-input" class="col-2 col-form-label">Quantity:</label>
    <div class="col-10">
      <input class="form-control" type="number" [(ngModel)]="product.quantity" id="quantity">
    </div>
  </div>
  <div class="form-group row">
    <label for="description">Description:</label><br>
    <textarea id="description" name="description" [(ngModel)]="pr.description" rows="4" cols="50"></textarea>
  </div>
  <div class="form-group row">
    <label for="example-email-input" class="col-2 col-form-label">Image:</label>
    <div class="col-10">
      <input class="form-control" type="text" [(ngModel)]="book.image" id="quantity">
    </div>
<div class="form-group row">
    <label for="bookType">Select book type!</label>
    <select class="form-control" id="bookType" [(ngModel)]="book.bookType">
      <option *ngFor="let type of bookTypeOptions" [ngValue]="type">{{ type }}</option>
    </select>
  </div>

  <div>
    <button type="button" class="btn btn-outline-success" (click)="createBook()">Create Book</button>
  </div>
