import { Component, Input } from '@angular/core';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { Product } from 'src/app/interfaces/product';
import { ProductService } from 'src/app/services/product/product.service';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.scss'],
  standalone: true,
  imports: [MatCardModule, MatButtonModule]
})
export class ProductComponent {
  // @Input() thisId!: string;

  product: Product = {
    name: '',
    price: 0,
    description: '',
    quantity: 0
  };

  constructor(private productService: ProductService) {}

  createProduct(): void {
    this.productService.createProduct(this.product)
      .subscribe(newProduct => {
        console.log('Product created:', newProduct);
        // Aici puteți face orice acțiune doriți după ce produsul a fost creat cu succes
      }, error => {
        console.error('Error creating product:', error);
        // Aici puteți gestiona eroarea, de exemplu, afișând un mesaj utilizatorului
      });
  }

}
<mat-card class="example-card">
  <mat-card-header>
    <div mat-card-avatar class="example-header-image"></div>
    <mat-card-title>Shiba Inu</mat-card-title>
    <mat-card-subtitle>Dog Breed</mat-card-subtitle>
  </mat-card-header>
  <img mat-card-image src="https://material.angular.io/assets/img/examples/shiba2.jpg" alt="Photo of a Shiba Inu"><br>
  <mat-card-content>
    <span style="color:#C2185B; font-size: 1.35em;">
      product id:
    </span><br>
    <span style="color:#C2185B; font-size: 1.35em;">
      {{thisId}}
    </span>
  </mat-card-content>
  <mat-card-actions>
    <button mat-button>LIKE</button>
    <button mat-button>SHARE</button>
  </mat-card-actions>
</mat-card>

