import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Wishlist } from 'src/app/interfaces/wishlist';

@Injectable({
  providedIn: 'root'
})
export class WishlistService {

  private apiUrl ='http://localhost:8080/api/wishlist';
  constructor(private http: HttpClient) {}

    createWishlist(): Observable<Wishlist> {
      return this.http.post<Wishlist>(`${this.apiUrl}`, null);

   }
   addGameToWishlist(wishlistId: number, gameId: number): Observable<Wishlist> {
    return this.http.patch<Wishlist>(`${this.apiUrl}/addGameToWishlist?wishlistId=${wishlistId}&gameId=${gameId}`, null);
  }
}
