import { Component, OnInit } from '@angular/core';
import { Book } from 'src/app/interfaces/book';
import { CartService } from 'src/app/services/cart/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent implements OnInit {
  books: Book[] = [];
  cartId: number | null = null;
  userId: number | null = null;

  constructor(private cartService: CartService) { }

  ngOnInit() {
    this.initializeUserData();
  }

  initializeUserData() {
    const userId = localStorage.getItem('userId');
    this.userId = userId ? parseInt(userId) : null;
    if (!this.userId) {
      console.error('User ID not found, please ensure the user is logged in.');
      return;
    }
    this.ensureCartExists();
  }

 .

  onAddBookToCart(bookId: number) {
    if (!this.userId) {
      console.error('User id is not defined');
      return;
    }
    this.ensureCartExists(bookId);
  }

  private addBookToCart(bookId: number) {
    if (!this.cartId) {
      console.error('Cart ID is not set or invalid. Cannot add book to cart.');
      return;
    }
    this.cartService.addBookToCart(this.cartId, bookId).subscribe(
      cart => {
        console.log('Book added to cart:', cart);
        // Optionally, refresh the list of books in the cart here
      },
      error => {
        console.error('Error adding book to cart:', error);
      }
    );
  }
}

<div *ngIf="books && books.length > 0">
  <h2>List of Books</h2>
  <ul>
    <li *ngFor="let book of books">
      <h3>{{ book.name }}</h3>
      <p><strong>Description:</strong> {{ book.description }}</p>
      <p><strong>Price:</strong> {{ book.price }}</p>
      <p><strong>Available:</strong> {{ book.quantity }} <strong>piece(s)</strong></p>
      <p><strong>Book Type:</strong> {{ book.bookType }}</p>
    </li>
  </ul>
</div>
