import { Component } from '@angular/core';
import { Book, BookType } from 'src/app/interfaces/book';
import { BookService } from 'src/app/services/book/book.service';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.scss']
})
export class BookComponent {
  book: Book = {
    id: '',
    name: '',
    bookType: BookType.ACADEMIC,
    description: '',
    price: 0,
    quantity: 0,
    imageUrl: '', // Elimină 'path/to/image.jpg'
    imageName: '' // Adaugă câmpul pentru numele imaginii
  };

  bookTypeOptions: string[] = Object.values(BookType);

  constructor(private bookService: BookService) { }

  onSubmit() {
    this.bookService.createBook(this.book, this.imageFile).subscribe(
      (response) => {
        console.log('Book created successfully:', response);
        // Faceți ceva cu răspunsul în cazul în care este necesar, cum ar fi redirecționarea către altă pagină sau afișarea unui mesaj de succes.
      },
      (error) => {
        console.error('Error creating book:', error);
        // Tratați eroarea aici, cum ar fi afișarea unui mesaj de eroare către utilizator.
      }
    );
  }

  onFileSelected(event: any) {
    this.imageFile = event.target.files[0];
  }
}

<div class="form-group row">
    <label for="example-text-input" class="col-2 col-form-label">Name of book:</label>
    <div class="col-10">
      <input class="form-control" type="text"[(ngModel)]="book.name" id="name" >
    </div>
  </div>
  <div class="form-group row">
    <label for="example-search-input" class="col-2 col-form-label">Price:</label>
    <div class="col-10">
      <input class="form-control" type="number" [(ngModel)]="book.price" id="price">
    </div>
  </div>
  <div class="form-group row">
    <label for="example-email-input" class="col-2 col-form-label">Quantity:</label>
    <div class="col-10">
      <input class="form-control" type="number" [(ngModel)]="book.quantity" id="quantity">
    </div>
  </div>
  <div class="form-group row">
    <label for="description">Description:</label><br>
    <textarea id="description" name="description" [(ngModel)]="book.description" rows="4" cols="50"></textarea>
  </div>
<div class="form-group row">
    <label for="bookType">Select book type!</label>
    <select class="form-control" id="bookType" [(ngModel)]="book.bookType">
      <option *ngFor="let type of bookTypeOptions" [ngValue]="type">{{ type }}</option>
    </select>
  </div>
  <div class="form-group row">
    <label for="image" class="col-2 col-form-label">Select Image:</label>
    <div class="col-10">
        <input type="file" accept="image/*" (change)="onFileSelected($event)">
    </div>
  <div>
    <button type="button" class="btn btn-outline-success" (click)="createBook()">Create Book</button>
  </div>
