import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Game } from 'src/app/interfaces/game';
import { Wishlist } from 'src/app/interfaces/wishlist';

@Injectable({
  providedIn: 'root'
})
export class WishlistService {

  private apiUrl ='http://localhost:8080/api/wishlist';
  constructor(private http: HttpClient) {}

  getMyWishlist(): Observable<Wishlist> {
    const headers = new HttpHeaders({
      'Authorization': 'Bearer ' + localStorage.getItem('token') 
    });
    return this.http.get<Wishlist>(`${this.apiUrl}/mywishlist`, { headers });
  }

  addBookToWishlist(wishlistId: number, bookId: number): Observable<Wishlist> {
    const headers = new HttpHeaders({
      'Authorization': 'Bearer ' + localStorage.getItem('token') // Asigură-te că token-ul este corect
    });
    const params = new HttpParams()
      .set('wishlistId', wishlistId.toString())
      .set('bookId', bookId.toString());
    return this.http.patch<Wishlist>(`${this.apiUrl}/addBookToCart`, {}, { headers, params });
  }
}
