import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Wishlist } from 'src/app/interfaces/wishlist';
import { WishlistService } from 'src/app/services/wishlist/wishlist.service';

@Component({
  selector: 'app-wishlist',
  templateUrl: './wishlist.component.html',
  styleUrls: ['./wishlist.component.scss']
})
export class WishlistComponent {



 books: any[] = [];
 games: any[] = [];
 products: any[] = [];
  wishlistId!: number;
  bookId: number=101;
  gameId: number=101;
  productId: number=101;


  wishlist!:Wishlist;


  constructor(private wishlistService: WishlistService, private router: Router, private route: ActivatedRoute) { }

  ngOnInit(): void {
    this.getMyWishlist();
  }

  getMyWishlist(): void {
    this.wishlistService.getMyWishlist().subscribe(
      (data: Wishlist) => {
        this.wishlist = data;
        console.log('Wishlist fetched successfully:', this.wishlist);
      },
      (error) => {
        console.error('Eroare la preluarea listei de favorite', error);
      }
    );
  }



  createWishlist(): void {
    this.wishlistService.createWishlist().subscribe(
      (wishlist: Wishlist) => {
        console.log('Wishlist created successfully:', wishlist);

      },
      error => {
        console.error('Error creating wishlist:', error);
      }
    );
  }
  addBookToWishlist() {
    this.wishlistService.addBookToWishlist(this.wishlistId, this.bookId).subscribe({
      next: (wishlist) => {
        console.log('Book added to wishlist', wishlist);
        this.router.navigate(['/wishlist']);

      },
      error: (error) => {
        console.error('Error adding book to wishlist', error);
      }
    });
  }
  removeBookFromWishlist(bookId: number): void {
    const wishlistId= this.wishlist.id;

    this.wishlistService.removeBookFromWishlist(wishlistId, bookId).subscribe(
      (updatedWishlist) => {
        this.wishlist = updatedWishlist;
        console.log('Book removed from wishlist successfully:', updatedWishlist);
      },
      (error) => {
        console.error('Error removing book from wishlist:', error);
      }
    );
  }
  addGameToWishlist() {
    this.wishlistService.addGameToWishlist(this.wishlistId, this.gameId).subscribe({
      next: (wishlist) => {
        console.log('Book added to wishlist', wishlist);
        this.router.navigate(['/wishlist']);

      },
      error: (error) => {
        console.error('Error adding game to wishlist', error);
      }
    });
  }
  removeGameFromWishlist(gameId: number): void {
    const wishlistId = this.wishlist.id;

    this.wishlistService.removeGameFromWishlist(wishlistId, gameId).subscribe(
      (updatedWishlist) => {
        this.wishlist = updatedWishlist;
        console.log('Game removed from wishlist successfully:', updatedWishlist);
      },
      (error) => {
        console.error('Error removing book from wishlist:', error);
      }
    );
  }
  addProductToWishlist() {
    this.wishlistService.addProductToWishlist(this.wishlistId, this.productId).subscribe({
      next: (wishlist) => {
        console.log('Product added to wishlist', wishlist);
        this.router.navigate(['/wishlist']);

      },
      error: (error) => {
        console.error('Error adding product to wishlist', error);
      }
    });
  }
  removeProductFromWishlist(productId: number): void {
    const wishlistId = this.wishlist.id;

    this.wishlistService.removeProductFromWishlist(wishlistId, productId).subscribe(
      (updatedWishlist) => {
        this.wishlist = updatedWishlist;
        console.log('Product removed from wishlist successfully:', updatedWishlist);
      },
      (error) => {
        console.error('Error removing product from wishlist:', error);
      }
    );
  }





}

<section style="background-color: #18917f;" *ngIf="wishlist">
  <div class="container h-100">
    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col">
        <p><span class="h2">Shopping Cart </span><span class="h4">({{ wishlist.books.length + wishlist.games.length + wishlist.products.length }} items in your wishlist)</span></p>

        <div class="card mb-4" *ngFor="let book of wishlist.books">
          <div class="card-body p-4">
            <div class="row align-items-center">
              <div class="col-md-2">
                <img [src]="book.image" class="img-fluid" alt="{{ book.name }}">
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Name</p>
                  <p class="lead fw-normal mb-0">{{ book.name }}</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Description</p>
                  <p class="lead fw-normal mb-0">{{ book.description }}</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Price</p>
                  <p class="lead fw-normal mb-0">{{ book.price }} RON</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <button (click)="removeBookFromWishlist(book.id)" class="btn btn-danger">Remove from Wishlist</button>
              </div>
            </div>
          </div>
        </div>

        <div class="card mb-4" *ngFor="let game of wishlist.games">
          <div class="card-body p-4">
            <div class="row align-items-center">
              <div class="col-md-2">
                <img [src]="game.image" class="img-fluid" alt="{{ game.name }}">
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Name</p>
                  <p class="lead fw-normal mb-0">{{ game.name }}</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Description</p>
                  <p class="lead fw-normal mb-0">{{ game.description }}</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Price</p>
                  <p class="lead fw-normal mb-0">{{ game.price }} RON</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <button (click)="removeGameFromWishlist(game.id)" class="btn btn-danger">Remove from Wishlist</button>
              </div>
            </div>
          </div>
        </div>

        <div class="card mb-4" *ngFor="let book of wishlist.books">
          <div class="card-body p-4">
            <div class="row align-items-center">
              <div class="col-md-2">
                <img [src]="book.image" class="img-fluid" alt="{{ book.name }}">
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Name</p>
                  <p class="lead fw-normal mb-0">{{ book.name }}</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Description</p>
                  <p class="lead fw-normal mb-0">{{ book.description }}</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Price</p>
                  <p class="lead fw-normal mb-0">{{ book.price }} RON</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <button (click)="removeBookFromWishlist(book.id)" class="btn btn-danger">Remove from Wishlist</button>
              </div>
            </div>
          </div>
        </div>

        <div class="d-flex justify-content-end">
          <button type="button" class="btn btn-light btn-lg me-2" [routerLink]="['/books']">Continue shopping</button>
