import { Component, OnInit } from '@angular/core';
import { Cart } from 'src/app/interfaces/cart';
import { Product } from 'src/app/interfaces/product';
import { CartService } from 'src/app/services/cart/cart.service';
import { ProductService } from 'src/app/services/product/product.service';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.scss']
})
export class ProductsComponent implements OnInit {
  products: Product[] = [];
  cartId!: number;

  constructor(private productService: ProductService, private cartService: CartService) { }

  ngOnInit(): void {
    this.getAllProducts();
  }

  getAllProducts(): void {
    this.productService.getAllProducts()
      .subscribe(products => this.products = products);
  }
  addToCart(productId: number): void {
    if (!this.cartId) {
      this.cartService.createCart().subscribe(
        (cart: Cart) => {
          console.log('Coșul de cumpărături a fost creat cu succes:', cart);

          this.cartService.addProductToCart(this.cartId, productId).subscribe(
            response => {
              console.log('Produs adăugat în coș cu succes:', response);
            },
            error => {
              console.error('Eroare la adăugarea produsului în coș:', error);
            }
          );
        },
        error => {
          console.error('Eroare la crearea coșului de cumpărături:', error);
        }
      );
    } else {
      this.cartService.addProductToCart(this.cartId, productId).subscribe(
        response => {
          console.log('Produs adăugat în coș cu succes:', response);
        },
        error => {
          console.error('Eroare la adăugarea produsului în coș:', error);
        }
      );
    }
  }

}
