import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Cart } from 'src/app/interfaces/cart';


@Injectable({
  providedIn: 'root'
})
export class CartService {

  private apiUrl = 'http://localhost:8080/api/carts';

  constructor(private http: HttpClient) { }


  createCart(): Observable<Cart> {
    return this.http.post<Cart>(this.apiUrl, {});
  }
  addBookToCart(cartId: number, bookId: number): Observable<Cart> {
    const url = `${this.apiUrl}/addBookToCart`;
    const params = { cartId: cartId.toString(), bookId: bookId.toString() };
    return this.http.patch<Cart>(url, {}, { params });
  }
  addProductToCart(cartId: number, productId: number): Observable<any> {
    const url = `${this.apiUrl}/add?cartId=${cartId}&productId=${productId}`;
    return this.http.patch(url, null);
  }
