<div class="form-group row">
  <label for="example-text-input" class="col-2 col-form-label">Name of book:</label>
  <div class="col-10">
    <input class="form-control" type="text"[(ngModel)]="book.name" id="name" >
  </div>
</div>
<div class="form-group row">
  <label for="example-search-input" class="col-2 col-form-label">Price:</label>
  <div class="col-10">
    <input class="form-control" type="number" [(ngModel)]="book.price" id="price">
  </div>
</div>
<div class="form-group row">
  <label for="example-email-input" class="col-2 col-form-label">Quantity:</label>
  <div class="col-10">
    <input class="form-control" type="number" [(ngModel)]="book.quantity" id="quantity">
  </div>
</div>
<div class="form-group row">
  <label for="description">Description:</label><br>
  <textarea id="description" name="description" [(ngModel)]="book.description" rows="4" cols="50"></textarea>
</div>
<div class="form-group row">
  <label for="bookType">Select book type!</label>
  <select class="form-control" id="bookType" [(ngModel)]="book.bookType">
    <option *ngFor="let type of bookTypeOptions" [ngValue]="type">{{ type }}</option>
  </select>
</div>
<div>
  <button type="button" class="btn btn-outline-success" (click)="createProduct()">Create Book</button>
</div>
