import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Book } from 'src/app/interfaces/book';
import { Cart } from 'src/app/interfaces/cart';
import { CartService } from 'src/app/services/cart/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent implements OnInit {
  books: any[] = [];

  games: any[] = [];
  cartId!: number;
  bookId: number=101;

  gameId: number=101;


  cart!:Cart;


  constructor(private cartService: CartService, private router: Router, private route: ActivatedRoute) { }

  ngOnInit(): void {
    this.getMyCart();
  }

  getMyCart(): void {
    this.cartService.getMyCart().subscribe(
      (data: Cart) => {
        this.cart = data;
        console.log('Cart fetched successfully:', this.cart);
      },
      (error) => {
        console.error('Eroare la preluarea listei de favorite', error);
      }
    );
  }

  removeBookFromCart(bookId: number): void {
    const cartId = this.cart.id;

    this.cartService.removeBookFromCart(cartId, bookId).subscribe(
      (updatedCart) => {
        this.cart = updatedCart;
        console.log('Book removed from cart successfully:', updatedCart);
      },
      (error) => {
        console.error('Error removing book from cart:', error);
      }
    );
  }

  removeGameFromCart(gameId: number): void {
    const cartId = this.cart.id;

    this.cartService.removeGameFromCart(cartId, gameId).subscribe(
      (updatedCart) => {
        this.cart = updatedCart;
        console.log('Game removed from cart successfully:', updatedCart);
      },
      (error) => {
        console.error('Error removing book from cart:', error);
      }
    );
  }


  createCart(): void {
    this.cartService.createCart().subscribe(
      (cart: Cart) => {
        console.log('Cart created successfully:', cart);

      },
      error => {
        console.error('Error creating cart:', error);
      }
    );
  }
  addBookToCart() {
    this.cartService.addBookToCart(this.cartId, this.bookId).subscribe({
      next: (cart) => {
        console.log('Book added to cart', cart);
        this.router.navigate(['/cart']);

      },
      error: (error) => {
        console.error('Error adding book to cart', error);
      }
    });
  }
  addGameToCart() {
    this.cartService.addGameToCart(this.cartId, this.gameId).subscribe({
      next: (cart) => {
        console.log('Book added to cart', cart);
        this.router.navigate(['/cart']);

      },
      error: (error) => {
        console.error('Error adding book to cart', error);
      }
    });
  }





}


<section class="vh-100" style="background-color: #18917f;">
  <div class="container h-100" *ngIf="cart">
    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col">
        <p><span class="h2">Shopping Cart </span><span class="h4">(1 item in your cart)</span></p>

        <div class="card mb-4"  >
          <div class="card-body p-4">

            <div class="row align-items-center">
              <div class="col-md-2">
                <ul>
                  <li *ngFor="let book of cart.books">
                    <img [src]="book.image" alt="{{ book.name }}" width="50">

                    <p>{{ book.description }}</p>
                    <p>Price: {{ book.price }} RON</p>
                    <button (click)="removeBookFromCart(book.id)">Remove from Cart</button>
                  </li>
                </ul>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Name</p>
                  <p class="lead fw-normal mb-0">iPad Air</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Color</p>
                  <p class="lead fw-normal mb-0"><i class="fas fa-circle me-2" style="color: #fdd8d2;"></i>
                    pink rose</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Quantity</p>
                  <p class="lead fw-normal mb-0">1</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Price</p>
                  <p class="lead fw-normal mb-0">$799</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Total</p>
                  <p class="lead fw-normal mb-0">$799</p>
                </div>
              </div>
            </div>

          </div>
        </div>

        <div class="card mb-5">
          <div class="card-body p-4">

            <div class="float-end">
              <p class="mb-0 me-5 d-flex align-items-center">
                <span class="small text-muted me-2">Order total:</span> <span
                  class="lead fw-normal">$799</span>
              </p>
            </div>

          </div>
        </div>

        <div class="d-flex justify-content-end">
          <button  type="button" data-mdb-button-init data-mdb-ripple-init class="btn btn-light btn-lg me-2">Continue shopping</button>
          <button  type="button" data-mdb-button-init data-mdb-ripple-init class="btn btn-primary btn-lg">Add to cart</button>
        </div>

      </div>
    </div>
  </div>
</section>

