import { Component, OnInit } from '@angular/core';
import { Game } from 'src/app/interfaces/game';
import { CartService } from 'src/app/services/cart/cart.service';
import { GameService } from 'src/app/services/game/game.service';
import { WishlistService } from 'src/app/services/wishlist/wishlist.service';

@Component({
  selector: 'app-games',
  templateUrl: './games.component.html',
  styleUrls: ['./games.component.scss']
})
export class GamesComponent implements OnInit {

  games: Game[] = [];
  gameTypeOptions: string[] = Object.values(GameType);
  selectType: string = '';
  cartId: number | null = null;

  constructor(
    private bookService: BookService,
    private cartService: CartService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.bookService.getAllBooks().subscribe(
      (books: Book[]) => {
        this.books = books;
      },
      (error) => {
        console.error('Error retrieving books:', error);
      }
    );

    this.cartService.getMyCart().subscribe(
      (cart) => {
        this.cartId = cart.id;
      },
      (error) => {
        console.error('Error fetching cart', error);
      }
    );
  }

  addToCart(book: Book): void {
    if (this.cartId === null) {
      console.error('Cart ID is not available. Unable to add book to cart.');
      return;
    }

    const bookId: number = book.id;

    this.cartService.addBookToCart(this.cartId, bookId).subscribe(
      response => {
        console.log('Cart updated successfully:', response);
        this.router.navigate(['/cart']);
      },
      error => {
        console.error('An error occurred while adding book to cart:', error);
      }
    );
  }
}

