import { Component } from '@angular/core';
import { Book } from 'src/app/interfaces/book';
import { CartService } from 'src/app/services/cart/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent {
  books: Book[] = [];
  cartId: number = 0;
  userId: number = 1;

  constructor(private cartService: CartService) { }

  onCreateCart(userId: number): void {
    this.cartService.createCart(userId).subscribe(
      response => {
        this.handleCartResponse(response);
      },
      error => {
        console.error('Error creating cart:', error);
      }
    );
  }

  onAddBookToCart(bookId: number): void {
    if (!this.userId) {
      console.error('User id is not defined');
      return;
    }

    if (!this.cartId) {
      this.cartService.createCart(this.userId).subscribe(
        response => {
          this.handleCartResponse(response, bookId);
        },
        error => {
          console.error('Error creating cart:', error);
        }
      );
    } else {
      this.cartService.addBookToCart(this.cartId, bookId).subscribe(
        cart => {
          console.log('Book added to cart:', cart);
        },
        error => {
          console.error('Error adding book to cart:', error);
        }
      );
    }
  }

  private handleCartResponse(response: any, bookId?: number): void {
    this.cartId = response.id;
    if (bookId) {
      this.cartService.addBookToCart(this.cartId, bookId).subscribe(
        cart => {
          console.log('Book added to cart:', cart);
        },
        error => {
          console.error('Error adding book to cart:', error);
        }
      );
    } else {
      console.log('Cart created successfully:', response);
    }
  }
}
<div *ngIf="books && books.length > 0">
  <h2>List of Books</h2>
  <ul>
    <li *ngFor="let book of books">
      <h3>{{ book.name }}</h3>
      <p><strong>Description:</strong> {{ book.description }}</p>
      <p><strong>Price:</strong> {{ book.price }}</p>
      <p><strong>Available:</strong> {{ book.quantity }} <strong>piece(s)</strong></p>
      <p><strong>Book Type:</strong> {{ book.bookType }}</p>
    </li>
  </ul>
</div>
