import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Product } from 'src/app/interfaces/product';


@Injectable({
  providedIn: 'root'
})
export class ProductService {
  private apiUrl = 'http://localhost:8080/api/products';
  constructor(private http: HttpClient) { }

  getAllBooks(): Observable<Book[]> {
    return this.http.get<Book[]>(`${this.url}/allBooks`);
  }
  createBook(book: Book): Observable<Book> {
    return this.http.post<Book>(`${this.url}/createBooks`, book);
  }

  getBooksByType(bookType: string): Observable<Book[]> {
    return this.http.get<Book[]>(`${this.url}/type/${bookType}`);
  }
  createProduct(product: Product): Observable<Product> {
    return this.http.post<Product>(`${this.apiUrl}/createProducts`, product);
  }
  getAllProducts(): Observable<Product[]> {
    return this.http.get<Product[]>(`${this.apiUrl}/allProducts`);
  }
  addProductToCart(cartId: number, productId: number): Observable<any> {
    const url = `${this.apiUrl}/add?cartId=${cartId}&productId=${productId}`;
    return this.http.patch(url, null);
  }
}
