import { Component, OnInit } from '@angular/core';
import { Book } from 'src/app/interfaces/book';
import { Cart } from 'src/app/interfaces/cart';
import { CartService } from 'src/app/services/cart/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent implements OnInit {
  books: any[] = [];
  cartId: number =1;
  bookId: number =101;


  constructor(private cartService: CartService) { }

  ngOnInit(): void {
    this.createCart();
    this.loadCart();
  }
  createCart(): void {
    this.cartService.createCart().subscribe(
      (cart: Cart) => {
        console.log('Cart created successfully:', cart);

      },
      error => {
        console.error('Error creating cart:', error);
      }
    );
  }
  addBookToCart() {
    this.cartService.addBookToCart(this.cartId, this.bookId).subscribe({
      next: (cart) => {
        console.log('Book added to cart', cart);
      },
      error: (error) => {
        console.error('Error adding book to cart', error);
      }
    });
  }
  loadCart() {
    this.cartService.getCartById(this.cartId).subscribe({
      next: (cart) => {
        this.books = cart.books; // presupunând că răspunsul include un obiect cart cu un array de cărți
      },
      error: (error) => console.error('Error loading the cart', error)
    });
  }


