import { Component } from '@angular/core';
import { RegisterService } from 'src/app/services/register/register.service';
import { User } from 'src/app/interfaces/user';
import { CartService } from 'src/app/services/cart/cart.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {

  user: User = {
    // Definiți aici proprietățile utilizatorului pentru înregistrare
  };

  constructor(private registerService: RegisterService, private cartService: CartService) { }

  registerUser(): void {
    this.registerService.registerUser(this.user)
      .subscribe(
        (response: User) => {
          console.log('User registered successfully:', response);
          // Extrageți userId-ul din răspunsul înregistrării
          const userId = response.userId;
          // Trimiteți userId către metoda onCreateCart() din CartComponent
          this.onCreateCart(userId);
        },
        error => {
          console.error('Error registering user:', error);
        }
      );
  }

  onCreateCart(userId: number): void {
    // Apelați metoda din CartService pentru a crea coșul pentru utilizatorul dat
    this.cartService.createCart(userId).subscribe(
      response => {
        console.log('Cart created successfully:', response);
      },
      error => {
        console.error('Error creating cart:', error);
      }
    );
  }
}
