import { Component } from '@angular/core';
import { CartService } from 'src/app/services/cart/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent {

  userId!: string;

  constructor(private cartService: CartService) { }

  onCreateCart(userId: string): void {
    this.cartService.createCart(userId).subscribe(
      response => {
        console.log('Cart created successfully:', response);
      },
      error => {
        console.error('Error creating cart:', error);
      }
    );
  }
  onAddBookToCart(cartId: string, bookId: string): void {
    this.cartService.addBookToCart(cartId, bookId)
      .subscribe(
        (cart) => {
          // Procesează răspunsul sau efectuează acțiuni suplimentare
          console.log('Cart updated:', cart);
        },
        (error) => {
          // Gestionare erori
          console.error('Error:', error);
        }
      );
  }

}
