import { HttpClient, HttpHeaders, HttpResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, catchError, throwError } from 'rxjs';
import { LoginData } from 'src/app/interfaces/login';

@Injectable({
  providedIn: 'root'
})
export class LoginService {

  private loginUrl = 'http://localhost:8080/api/auth/login';


  constructor(private httpReq:HttpClient) {
  }
  login(email: string, password: string): Observable<any> {
    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });
    const body = JSON.stringify({ email, password });

    return this.httpReq.post(this.loginUrl, body, { headers }).pipe(
      map((response: any) => {
        // Stochează token-ul JWT în local storage
        localStorage.setItem('token', response.token);
        return response;
      })
    );
  }

  logout(): void {
    // Șterge token-ul din local storage la logout
    localStorage.removeItem('token');
  }

  // Metodă pentru a obține token-ul
  getToken(): string | null {
    return localStorage.getItem('token');
  }

  // Metodă pentru a verifica dacă utilizatorul este autentificat
  isAuthenticated(): boolean {
    return !!this.getToken();
  }



}
