import { Component, OnInit } from '@angular/core';
import { Product } from 'src/app/interfaces/product';
import { CartService } from 'src/app/services/cart/cart.service';
import { ProductService } from 'src/app/services/product/product.service';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.scss']
})
export class ProductsComponent implements OnInit {
  products: Product[] = [];

  constructor(private productService: ProductService, private cartService: CartService) { }

  ngOnInit(): void {
    this.getAllProducts();
  }

  getAllProducts(): void {
    this.productService.getAllProducts()
      .subscribe(products => this.products = products);
  }

  addToCart(cartId: number, productId: number): void {
    this.cartService.addProductToCart(cartId, productId)
      .subscribe(
        response => {
          console.log('Produs adăugat în coș cu succes:', response);
          // Poți face orice alte acțiuni necesare aici după ce produsul este adăugat în coș
        },
        error => {
          console.error('Eroare la adăugarea produsului în coș:', error);
        }
      );
  }
}

<!-- product-list.component.html -->

<ul class="product-list">
    <li class="product-item" *ngFor="let product of products">
      <img [src]="product.image || 'placeholder.jpg'" alt="{{ product.name }}">
      <div class="name">{{ product.name }}</div>
      <div class="description">{{ product.description }}</div>
      <div class="price">RON {{ product.price }}</div>
      <div class="quantity">Available quantity: {{ product.quantity }}</div>
      <div *ngFor="let product of products">
        .
        <button (click)="addToCart(cartId, product.id)">Add to Cart</button>
      </div>
    </li>
  </ul>
