import { Component, OnInit } from '@angular/core';
import { Book } from 'src/app/interfaces/book';
import { CartService } from 'src/app/services/cart/cart.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent implements OnInit {
  books: Book[] = [];
  cartId: number | null = null;
  userId: number | null = null;

  constructor(private cartService: CartService, private router: Router) { }

  ngOnInit() {
    this.loadUserAndCartData();
  }

  loadUserAndCartData() {
    this.userId = +localStorage.getItem('userId');
    if (!this.userId) {
      console.error('User ID not found, please log in.');
      this.router.navigate(['/login']);  // redirect to login page or appropriate route
      return;
    }

    this.cartService.getCartByUserId(this.userId).subscribe(
      cart => {
        this.cartId = cart.id;
        this.books = cart.books;
      },
      error => {
        if (error.status === 404) {
          this.createCart();
        } else {
          console.error('Error retrieving cart:', error);
        }
      }
    );
  }

  createCart() {
    this.cartService.createCart(this.userId as number).subscribe(
      cart => {
        this.cartId = cart.id;
        this.books = [];
        console.log('New cart created successfully:', cart);
      },
      error => {
        console.error('Failed to create a new cart:', error);
      }
    );
  }

  onAddBookToCart(bookId: number) {
    if (!this.cartId) {
      console.error('Cart ID is not set. Attempting to create a new cart.');
      this.createCart();
      return;
    }
    this.cartService.addBookToCart(this.cartId, bookId).subscribe(
      response => {
        this.books.push(response.book); // Assuming the backend returns the added book
        console.log('Book added to cart:', response);
      },
      error => {
        console.error('Error adding book to cart:', error);
      }
    );

  }
}

<div *ngIf="books && books.length > 0">
  <h2>List of Books</h2>
  <ul>
    <li *ngFor="let book of books">
      <h3>{{ book.name }}</h3>
      <p><strong>Description:</strong> {{ book.description }}</p>
      <p><strong>Price:</strong> {{ book.price }}</p>
      <p><strong>Available:</strong> {{ book.quantity }} <strong>piece(s)</strong></p>
      <p><strong>Book Type:</strong> {{ book.bookType }}</p>
    </li>
  </ul>
</div>
