import { HttpClient } from '@angular/common/http';
import { Injectable, OnInit } from '@angular/core';
import { LoginComponent } from 'src/app/components/login/login.component';

@Injectable({
  providedIn: 'root'
})
export class RegisterService implements OnInit{
  apiUrl = 'http://localhost:8080/api/register';
  login= false;

  constructor(private http: HttpClient, private loginComponet: LoginComponent) { }

  ngOnInit(): void {
    this.login = this.l.isLoggedIn(); // Verifică starea de autentificare la inițializarea componentei
  }

  registerUser(user: any) {
    return this.http.post<any>(this.apiUrl, user);
  }

}
