import { Component } from '@angular/core';
import { CartService } from 'src/app/services/cart/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent {

  userId!: string;

  constructor(private cartService: CartService) { }

  onCreateCart(userId: string): void {
    this.cartService.createCart(userId).subscribe(
      response => {
        console.log('Cart created successfully:', response);
      },
      error => {
        console.error('Error creating cart:', error);
      }
    );
  }
  onAddBookToCart(cartId: string, bookId: string): void {
    this.cartService.addBookToCart(cartId, bookId)
      .subscribe(
        (cart) => {
          // Procesează răspunsul sau efectuează acțiuni suplimentare
          console.log('Cart updated:', cart);
        },
        (error) => {
          // Gestionare erori
          console.error('Error:', error);
        }
      );
  }

}
<span style="display: flex; margin-left: 10px;">
  <h1>cart works !</h1>

  <div>
    <h2>Creare Coș Cumpărături</h2>
    <form #cartForm="ngForm" >
      <div class="form-group">
        <label for="userId">ID Utilizator:</label>
        <input type="text" class="form-control" id="userId" name="userId" [(ngModel)]="userId" required>
      </div>
      <button type="submit" class="btn btn-primary">Creare Coș Cumpărături</button>
    </form>
  </div>

  <div *ngIf="cartItems.length > 0; else emptyCart">
    <h2>Cos de cumpărături</h2>
    <ul>
      <li *ngFor="let cartItem of cartItems">
        {{ cartItem.book.title }} - {{ cartItem.book.author }} - Preț: {{ cartItem.book.price }}
        <!-- Alte detalii despre carte -->
      </li>
    </ul>
    <p>Total: {{ calculateTotalPrice() }}</p>
    <button (click)="checkout()">Finalizare comandă</button>
  </div>

  <ng-template #emptyCart>
    <p>Cosul de cumpărături este gol.</p>
  </ng-template>


</span>
