import { Component, OnInit } from '@angular/core';
import { Game } from 'src/app/interfaces/game';
import { CartService } from 'src/app/services/cart/cart.service';
import { GameService } from 'src/app/services/game/game.service';

@Component({
  selector: 'app-games',
  templateUrl: './games.component.html',
  styleUrls: ['./games.component.scss']
})
export class GamesComponent implements OnInit {

  games: Game[] = [];

  constructor(private gameService: GameService, private cartService :CartService, private wishlist: ) { }

  ngOnInit(): void {
    this.gameService.getAllGames().subscribe(
      (games: Game[]) => {
        this.games = games;
      },
      (error) => {
        console.error('Error retrieving games:', error);
      }
    );
  }
  addToCart(game:Game): void {
    const cartId: number=0;
    const gameId:number=game.id;


    this.cartService.addGameToCart(cartId, gameId).subscribe(
      response => {
        console.log('Cart updated successfully:', response);

      },
      error => {
        console.error('An error occurred while adding book to cart:', error);

      }
    );
  }

  addToWishlist(game: Game): void {
    const wishlistId: number = 0; // sau orice altceva care reprezintă ID-ul listei de dorințe
    const gameId: number = game.id;

    this.wishlistService.addGameToWishlist(wishlistId, gameId).subscribe(
      (response) => {
        console.log('Game added to wishlist successfully:', response);
      },
      (error) => {
        console.error('An error occurred while adding game to wishlist:', error);
      }
    );
  }


}
