<div class="container">



<div class="modal fade" id="cartModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog modal-lg modal-dialog-centered" role="document">
    <div class="modal-content">
      <div class="modal-header border-bottom-0">
        <h5 class="modal-title" id="exampleModalLabel">
          Your Shopping Cart
        </h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body" *ngIf="cart">
        <h2>My Cart</h2>
        <ul>
          <ng-container *ngIf="cart.books && cart.books.length > 0">
            <li *ngFor="let book of cart.books">
              <img [src]="book.image" alt="{{ book.name }}" width="50">
              <h3>{{ book.name }}</h3>
              <p>{{ book.description }}</p>
              <p>Price: {{ book.price }} RON</p>
              <p>Quantity: {{ book.quantity }}</p>
              <button (click)="removeBookFromCart(book.id)">Remove from Cart</button>
            </li>
          </ng-container>
          <ng-container *ngIf="cart.games && cart.games.length > 0">
            <li *ngFor="let game of cart.games">
              <img [src]="game.image" alt="{{ game.name }}" width="50">
              <h3>{{ game.name }}</h3>
              <p>{{ game.description }}</p>
              <p>Price: {{ game.price }} RON</p>
              <p>Quantity: {{ game.quantity }}</p>
              <button (click)="removeGameFromCart(game.id)">Remove from Cart</button>
            </li>
          </ng-container>
        </ul>

        <p>Total: {{ cart.total }} RON</p>
      </div>

      <div *ngIf="!cart">
        <p>Your cart is empty.</p>
        <div class="d-flex justify-content-end">
          <h5>Total: <span class="price text-success">89$</span></h5>
        </div>
      </div>
      <div class="modal-footer border-top-0 d-flex justify-content-between">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-success">Checkout</button>
      </div>
    </div>
  </div>
</div>
