import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Route, Router } from '@angular/router';
import { LoginData } from 'src/app/interfaces/login';
import { CartService } from 'src/app/services/cart/cart.service';
import { LoginService } from 'src/app/services/login/login.service';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {

  user: LoginData = { email: '', password: '' }; // Define a user object for validation

  constructor(private loginService: LoginService, private cartService:CartService, private router: Router) { }

  login(username: string, password: string): void {
    this.authService.login(username, password).subscribe(
      (response) => {
        console.log('Utilizator autentificat cu succes:', response);
        // După autentificare cu succes, creăm un coș nou pentru utilizator
        this.createCartForUser();
      },
      (error) => {
        console.error('Eroare la autentificare:', error);
      }
    );
  }

  createCartForUser(): void {
    this.cartService.createCart().subscribe(
      (cart) => {
        console.log('Coș creat cu succes pentru utilizator.');
        // Poți face orice alte acțiuni necesare aici după crearea coșului pentru utilizator
      },
      (error) => {
        console.error('Eroare la crearea coșului pentru utilizator:', error);
      }
    );
  }
}


