import { Component } from '@angular/core';
import { RegisterService } from 'src/app/services/register/register.service';
import { User, UserRole } from 'src/app/interfaces/user';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})

export class RegisterComponent {


    user: User = {
     userId: 0,
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    phoneNumber: '',
    role:UserRole.USER
  };
  userRoles = [
    { label: 'Admin', value: UserRole.ADMIN },
    { label: 'User', value: UserRole.USER }
];

  constructor(private registerService: RegisterService, private cartService: CartServiceprivate router: Router) { }

  registerUser(): void {
    this.registerService.registerUser(this.user)
      .subscribe(
        (response) => { // Eliminați tipul de date specificat și lăsați TypeScript să-l deducă automat
          console.log('User registered successfully:', response);
          const userId = response.id; // Extrageți userId-ul din răspunsul înregistrării
          // Apelați metoda de creare a coșului folosind userId-ul obținut
          this.onCreateCart(userId);
          this.router.navigate(['/home']);
        },
        error => {
          console.error('Error registering user:', error);
        }
      );
    }

}
