import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Book } from 'src/app/interfaces/book';
import { Cart } from 'src/app/interfaces/cart';
import { CartService } from 'src/app/services/cart/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent implements OnInit {
  books: any[] = [];

  games: any[] = [];
  cartId!: number;
  bookId: number=101;

  gameId: number=101;


  cart!:Cart;


  constructor(private cartService: CartService, private router: Router, private route: ActivatedRoute) { }

  ngOnInit(): void {
    this.getMyCart();
  }

  getMyCart(): void {
    this.cartService.getMyCart().subscribe(
      (data: Cart) => {
        this.cart = data;
        console.log('Cart fetched successfully:', this.cart);
      },
      (error) => {
        console.error('Eroare la preluarea listei de favorite', error);
      }
    );
  }

  removeBookFromCart(bookId: number): void {
    const cartId = this.cart.id;

    this.cartService.removeBookFromCart(cartId, bookId).subscribe(
      (updatedCart) => {
        this.cart = updatedCart;
        console.log('Book removed from cart successfully:', updatedCart);
      },
      (error) => {
        console.error('Error removing book from cart:', error);
      }
    );
  }

  removeGameFromCart(gameId: number): void {
    const cartId = this.cart.id;

    this.cartService.removeGameFromCart(cartId, gameId).subscribe(
      (updatedCart) => {
        this.cart = updatedCart;
        console.log('Game removed from cart successfully:', updatedCart);
      },
      (error) => {
        console.error('Error removing book from cart:', error);
      }
    );
  }


  createCart(): void {
    this.cartService.createCart().subscribe(
      (cart: Cart) => {
        console.log('Cart created successfully:', cart);

      },
      error => {
        console.error('Error creating cart:', error);
      }
    );
  }
  addBookToCart() {
    this.cartService.addBookToCart(this.cartId, this.bookId).subscribe({
      next: (cart) => {
        console.log('Book added to cart', cart);
        this.router.navigate(['/cart']);

      },
      error: (error) => {
        console.error('Error adding book to cart', error);
      }
    });
  }
  addGameToCart() {
    this.cartService.addGameToCart(this.cartId, this.gameId).subscribe({
      next: (cart) => {
        console.log('Book added to cart', cart);
        this.router.navigate(['/cart']);

      },
      error: (error) => {
        console.error('Error adding book to cart', error);
      }
    });
  }





}



<section class="vh-100" style="background-color: #18917f;" *ngIf="cart">
  <div class="container h-100">
  <div class="card mb-4" *ngFor="let book of cart.books">
    <div class="card mb-4" *ngFor="let game of cart.games">

    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col">
        <p><span class="h2">Shopping Cart </span><span class="h4">({{ cart.books.length + cart.games.length }} items in your cart)</span></p>


          <div class="card-body p-4">
            <div class="row align-items-center">
              <div class="col-md-2">
                <img [src]="book.image" class="img-fluid" alt="{{ book.name }}">
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Name</p>
                  <p class="lead fw-normal mb-0">{{ book.name }}</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Description</p>
                  <p class="lead fw-normal mb-0">{{ book.description }}</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Price</p>
                  <p class="lead fw-normal mb-0">{{ book.price }} RON</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <button (click)="removeBookFromCart(book.id)" class="btn btn-danger">Remove from Cart</button>
              </div>
            </div>
          </div>
        </div>


          <div class="card-body p-4">
            <div class="row align-items-center">
              <div class="col-md-2">
                <img [src]="game.image" class="img-fluid" alt="{{ game.name }}">
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Name</p>
                  <p class="lead fw-normal mb-0">{{ game.name }}</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Description</p>
                  <p class="lead fw-normal mb-0">{{ game.description }}</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <div>
                  <p class="small text-muted mb-4 pb-2">Price</p>
                  <p class="lead fw-normal mb-0">{{ game.price }} RON</p>
                </div>
              </div>
              <div class="col-md-2 d-flex justify-content-center">
                <button (click)="removeGameFromCart(game.id)" class="btn btn-danger">Remove from Cart</button>
              </div>
            </div>
          </div>
        </div>

        <div class="d-flex justify-content-end">
          <button type="button" class="btn btn-light btn-lg me-2" [routerLink]="['/books']">Continue shopping</button>
          <button type="button" class="btn btn-primary btn-lg">Checkout</button>
        </div>
      </div>
    </div>
  </div>
</section>

<div *ngIf="!cart">
  <p>Your cart is empty.</p>
</div>


