import { Component } from '@angular/core';
import { Book, BookType } from 'src/app/interfaces/book';
import { BookService } from 'src/app/services/book/book.service';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.scss']
})
export class BookComponent {
  book: Book= {
    id: '',
    name: '',
    bookType: BookType.ACADEMIC,
    description: '',
    price: 0,
    quantity: 0
  };

  bookTypeOptions: string[] = Object.values(BookType);

  constructor(private bookService: BookService) { }

  createBook() {
    this.bookService.createBook(this.book).subscribe(
      response => {
        console.log('Book created successfully:', response);
      },
      error => {
        console.error('Error creating book:', error);
      }
    );

}
}

<div class="form-group row">
    <label for="example-text-input" class="col-2 col-form-label">Name of book:</label>
    <div class="col-10">
      <input class="form-control" type="text" value="Artisanal kale" id="example-text-input">
    </div>
  </div>
  <div class="form-group row">
    <label for="example-search-input" class="col-2 col-form-label">Price:</label>
    <div class="col-10">
      <input class="form-control" type="search" value="How do I shoot web" id="example-search-input">
    </div>
  </div>
  <div class="form-group row">
    <label for="example-email-input" class="col-2 col-form-label">Quantity:</label>
    <div class="col-10">
      <input class="form-control" type="email" value="bootstrap@example.com" id="example-email-input">
    </div>
  </div>
  <div class="form-group row">
    <label for="description">Description:</label><br>
    <textarea id="description" name="description" [(ngModel)]="book.description" rows="4" cols="50"></textarea>
  </div>
