import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Book } from 'src/app/interfaces/book';
import { Cart } from 'src/app/interfaces/cart';
import { CartService } from 'src/app/services/cart/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent implements OnInit {
  books: any[] = [];

  games: any[] = [];
  cartId!: number;
  bookId: number=101;

  gameId: number=101;


  cart!:Cart;


  constructor(private cartService: CartService, private router: Router, private route: ActivatedRoute) { }

  ngOnInit(): void {
    this.getMyCart();
  }

  getMyCart(): void {
    this.cartService.getMyCart().subscribe(
      (data: Cart) => {
        this.cart = data;
        console.log('Cart fetched successfully:', this.cart);
      },
      (error) => {
        console.error('Eroare la preluarea listei de favorite', error);
      }
    );
  }

  removeBookFromCart(bookId: number): void {
    const cartId = this.cart.id;

    this.cartService.removeBookFromCart(cartId, bookId).subscribe(
      (updatedCart) => {
        this.cart = updatedCart;
        console.log('Book removed from cart successfully:', updatedCart);
      },
      (error) => {
        console.error('Error removing book from cart:', error);
      }
    );
  }

  removeGameFromCart(gameId: number): void {
    const cartId = this.cart.id;

    this.cartService.removeGameFromCart(cartId, gameId).subscribe(
      (updatedCart) => {
        this.cart = updatedCart;
        console.log('Game removed from cart successfully:', updatedCart);
      },
      (error) => {
        console.error('Error removing book from cart:', error);
      }
    );
  }


  createCart(): void {
    this.cartService.createCart().subscribe(
      (cart: Cart) => {
        console.log('Cart created successfully:', cart);

      },
      error => {
        console.error('Error creating cart:', error);
      }
    );
  }
  addBookToCart() {
    this.cartService.addBookToCart(this.cartId, this.bookId).subscribe({
      next: (cart) => {
        console.log('Book added to cart', cart);
        this.router.navigate(['/cart']);

      },
      error: (error) => {
        console.error('Error adding book to cart', error);
      }
    });
  }
  addGameToCart() {
    this.cartService.addGameToCart(this.cartId, this.gameId).subscribe({
      next: (cart) => {
        console.log('Book added to cart', cart);
        this.router.navigate(['/cart']);

      },
      error: (error) => {
        console.error('Error adding book to cart', error);
      }
    });
  }





}


<div class="modal fade" id="cartModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog modal-lg modal-dialog-centered" role="document">
    <div class="modal-content">
      <div class="modal-header border-bottom-0">
        <h5 class="modal-title" id="exampleModalLabel">
          Your Shopping Cart
        </h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div *ngIf="cart" class="modal-body">
        <h2>My Cart</h2>
  <ul>
    <li *ngFor="let book of cart.books">
      <img [src]="book.image" alt="{{ book.name }}" width="50">
      <h3>{{ book.name }}</h3>
      <p>{{ book.description }}</p>
      <p>Price: {{ book.price }} RON</p>
      <button (click)="removeBookFromCart(book.id)">Remove from Cart</button>
    </li>
    <li *ngFor="let game of cart.games">
      <img [src]="game.image" alt="{{ game.name }}" width="50">
      <h3>{{ game.name }}</h3>
      <p>{{ game.description }}</p>
      <p>Price: {{ game.price }} RON</p>

      <button (click)="removeGameFromCart(game.id)">Remove from Cart</button>
    </li>
  </ul>
          <tbody>
            <tr>
              <td class="w-25">
                <img src="https://s3.eu-central-1.amazonaws.com/bootstrapbaymisc/blog/24_days_bootstrap/vans.png" class="img-fluid img-thumbnail" alt="Sheep">
              </td>
              <td>Vans Sk8-Hi MTE Shoes</td>
              <td>89$</td>
              <td class="qty"><input type="text" class="form-control" id="input1" value="2"></td>
              <td>178$</td>
              <td>
                <a href="#" class="btn btn-danger btn-sm">
                  <i class="fa fa-times"></i>
                </a>
              </td>
            </tr>
          </tbody>

        <div class="d-flex justify-content-end">
          <h5>Total: <span class="price text-success">89$</span></h5>
        </div>
      </div>
      <div class="modal-footer border-top-0 d-flex justify-content-between">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-success">Checkout</button>
      </div>
    </div>
  </div>
</div>








