import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { LoginData } from 'src/app/interfaces/login';
import { LoginService } from 'src/app/services/login/login.service';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  loginForm: FormGroup;

    user: LoginData = {
      email: '',
      password: ''
    };

  constructor(private http: HttpClient, private formBuilder: FormBuilder, private loginService: LoginService) {

    this.loginForm = this.formBuilder.group({
      username: ['', Validators.required],
      password: ['', Validators.required]
    });
  }


  onSubmit():void {
    if (this.loginForm.valid) {}
        const email = this.loginForm.get('email')?.value;
        const password = this.loginForm.get('password')?.value; 

        this.http.post<any>('https://example.demo.com/controller/login', { email, password })
        .subscribe(
          response => {
            // Gestionați răspunsul de la server
            console.log('Răspuns de la server:', response);
          },
          error => {
            // Gestionați erorile
            console.error('Eroare:', error);
          }
        );
  }

}
