import { Component, OnInit } from '@angular/core';
import { Book, BookType } from 'src/app/interfaces/book';
import { BookService } from 'src/app/services/book/book.service';
import { CartService } from 'src/app/services/cart/cart.service';

@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.scss']
})
export class BooksComponent implements OnInit{
  book: Book = {
    id: 0,
    name: '',
    bookType: BookType.ACADEMIC,
    description: '',
    price: 0,
    quantity: 0,
    image: '' 
  };

  books: Book[] = [];
  bookTypeOptions: string[] = Object.values(BookType);
  selectBookType: string= '';

  constructor(private bookService: BookService, private cartService: CartService) {}

  ngOnInit(): void {
    this.bookService.getAllBooks().subscribe(
      (books: Book[]) => {
        this.books =books;
      },
      (error) => {
        console.error('Error retrieving books:', error);
      }
    );
  }


  addToCart(book: Book): void {
    const cartId: number=0;
    const bookId:number=book.id;


    this.cartService.addBookToCart(cartId, bookId).subscribe(
      response => {
        console.log('Cart updated successfully:', response);

      },
      error => {
        console.error('An error occurred while adding book to cart:', error);

      }
    );

  // addToCart(book: Book): void {
