import { Component, OnInit } from '@angular/core';
import { Game } from 'src/app/interfaces/game';
import { CartService } from 'src/app/services/cart/cart.service';
import { GameService } from 'src/app/services/game/game.service';

@Component({
  selector: 'app-games',
  templateUrl: './games.component.html',
  styleUrls: ['./games.component.scss']
})
export class GamesComponent implements OnInit {

  games: Game[] = [];

  constructor(private gameService: GameService, private cartService :CartService) { }

  ngOnInit(): void {
    this.gameService.getAllGames().subscribe(
      (games: Game[]) => {
        this.games = games;
      },
      (error) => {
        console.error('Error retrieving games:', error);
      }
    );
  }
  addToCart(game:Game): void {
    const cartId: number=0;
    const gameId:number=game.id;


    this.cartService.addGameToCart(cartId, gameId).subscribe(
      response => {
        console.log('Cart updated successfully:', response);

      },
      error => {
        console.error('An error occurred while adding book to cart:', error);

      }
    );
  }

}



<div *ngIf="games && games.length > 0">
    <h2>List of Games</h2>
    <ul>
      <li *ngFor="let game of games">
        <img [src]="game.image" alt="Game Image" class="game-image">
        <h3>{{ game.name }}</h3>
        <p><strong>Description:</strong> {{ game.description }}</p>
        <p><strong>Price:</strong> {{ game.price }}</p>
        <p><strong>Aviable:</strong> {{ game.quantity }} <strong>pace</strong> </p>
        <p><strong>Game Type:</strong> {{ game.gameType }}</p>
        <button (click)="addToCart(game)">Adaugă în coș</button>
      </li>

    </ul>
  </div>



  <ul class="product-list">
    <li class="product-item" *ngFor="let product of products">
<section style="background-color: #eee;">
  <div class="container py-5">
    <div class="row justify-content-center mb-3">
      <div class="col-md-12 col-xl-10">
        <div class="card shadow-0 border rounded-3">
          <div class="card-body">
            <div class="row">
              <div class="col-md-12 col-lg-3 col-xl-3 mb-4 mb-lg-0">
                <div class="bg-image hover-zoom ripple rounded ripple-surface">
                  <img [src]="product.image || 'placeholder.jpg'" alt="{{ product.name }}"
                    class="" />
                  <a href="#!">
                    <div class="hover-overlay">
                      <div class="mask" style="background-color: rgba(253, 253, 253, 0.15);"></div>
                    </div>
                  </a>
                </div>
              </div>
              <div class="col-md-6 col-lg-6 col-xl-6">
                <div class="name">{{ product.name }}</div>
                <div class="d-flex flex-row">
                  <div class="text-danger mb-1 me-2">
                    <i class="fa fa-star"></i>
                    <i class="fa fa-star"></i>
                    <i class="fa fa-star"></i>
                    <i class="fa fa-star"></i>
                  </div>
                  <div class="quantity">Available quantity: {{ product.quantity }}</div>
                </div>
                <div class="mt-1 mb-0 text-muted small">
                  <div class="description">{{ product.description }}</div>
                </div>

              </div>
              <div class="col-md-6 col-lg-3 col-xl-3 border-sm-start-none border-start">
                <div class="d-flex flex-row align-items-center mb-1">

                  <div class="price"> {{ product.price }} RON</div>
                </div>
                <h6 class="text-success">Free shipping</h6>
                <div class="d-flex flex-column mt-4">
                  <button data-mdb-button-init data-mdb-ripple-init class="btn btn-primary btn-sm" type="button"(click)="addToCart(product.id)">Add to Cart

                  </button>
                  <button data-mdb-button-init data-mdb-ripple-init class="btn btn-outline-primary btn-sm mt-2" type="button">
                    Add to wishlist
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
