import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Cart

@Injectable({
  providedIn: 'root'
})
export class CartService {

  private apiUrl = 'http://localhost:8080/api/carts';

  constructor(private http: HttpClient) { }

  createCart(userId: string): Observable<any> {
    const headers = new HttpHeaders({
      'Content-Type': 'application/json'
    });

    const body = { userId: userId };

    return this.http.post<any>(this.apiUrl, body, { headers: headers });
  }
  addBookToCart(cartId: string, bookId: string): Observable<Cart> {
    const url = `/addBook?cartId=${cartId}&bookId=${bookId}`;
    return this.http.patch<Cart>(url, {});
  }
}
